function openFile(e){"use strict";var t=new XMLHttpRequest,n=e.file,o=e.callback,r=e.format||n.split(".").slice(-1)[0];try{t.open("GET",n)}catch(e){return console.error(e),console.log("Attempt failed!"),-1}return t.send(),t.onreadystatechange=function(){if(4===t.readyState)try{o({content:t.responseText,file:n,fileFormat:r})}catch(e){return console.error(e),console.log("No function was passed as argument."),-1}},t}